var sys = require('sys'),
    express = require('express'),
    gzip = require('connect-gzip'),
    gravatar = require('gravatar'),
    everyauth = require('everyauth'),
    conf = require(__dirname + '/../../../example/conf');

everyauth.debug = true;
everyauth.twitter
  .consumerKey(conf.twit.consumerKey)
  .consumerSecret(conf.twit.consumerSecret)
  .findOrCreateUser( function (session, accessToken, accessTokenSecret, twitterUserMetadata) {
    console.log(twitterUserMetadata);
  })
  .redirectPath('/');

var app = express.createServer();//,
//    io = require('socket.io').listen(app);

app.set('view engine', 'jade');
//app.use(gzip.gzip());
app.use(express.bodyParser());
app.use(express.cookieParser());
app.use(gzip.staticGzip(__dirname + '/static',{maxAge: 8640000000}));
app.use(express.session({
  secret: 'xxx',
  fingerprint: function fingerprint(req) {
    var result = '';
    return(result);
  }
}));
app.use(everyauth.middleware());
//express.errorHandler({ dumpExceptions: false, showStack: false });

app.get('/', function(req,res) {
  console.log(req.session);
  var options = {
          layout: false,
          locals: {
            gravatar: gravatar.url('s.reuter@geek-it.de', {s: '150'})
          }
        }
  res.render('main', options);
});

everyauth.helpExpress(app);


app.listen(3000);


